mapper.select:
select ac.id, ac.user_name, ac.date, cs.second_category_name, ac.in_ex_status, ac.amount, at.account_type_name, ac.remarks from account_current ac, category_second cs, account_type at where ac.user_name = cs.user_name and ac.account_book_id = cs.account_book_id and ac.second_category_id = cs.id and ac.account_type_id = at.id and ac.user_name = #{userName} and ac.account_book_id = #{accountBookId}
and DATE_FORMAT( date, '%Y-%m' ) = #{dateConverted}
ORDER BY ac.date DESC limit #{startIndex},#{pagesize}

select count(*) from account_current ac, category_second cs, account_type at where ac.user_name = cs.user_name and ac.account_book_id = cs.account_book_id and ac.second_category_id = cs.id and ac.account_type_id = at.id and ac.user_name = #{userName} and ac.account_book_id = #{accountBookId}
AND ac.first_category_id = #{firstCategoryId}
AND ac.second_category_id = #{secondCategoryId}
AND ac.account_type_id = #{accountTypeId}
AND date_format(ac.date, '%Y-%m-%d') BETWEEN #{startDate} AND #{endDate}
AND ac.remarks like concat('%', #{remarks}, '%')

SELECT ac.id, ac.user_name, ac.date, cs.second_category_name, ac.in_ex_status, ac.amount, AT.account_type_name, ac.remarks FROM account_current ac, category_first cf, category_second cs, account_type AT WHERE ac.user_name = cs.user_name AND ac.account_book_id = cs.account_book_id AND ac.first_category_id = cf.id AND ac.second_category_id = cs.id AND ac.account_type_id = AT.id AND ac.user_name = #{userName} AND ac.account_book_id = #{accountBookId}
AND ac.first_category_id = #{firstCategoryId}
AND ac.second_category_id = #{secondCategoryId}
AND ac.account_type_id = #{accountTypeId}
AND date_format(ac.date, '%Y-%m-%d') BETWEEN #{startDate} AND #{endDate}
AND ac.remarks like concat('%', #{remarks}, '%')
and DATE_FORMAT( date, '%Y-%m-%d' ) = #{date}
ORDER BY ac.date DESC limit #{startIndex},#{pagesize}
