mapper.update:

update account_financial
set user_name = #{userName,jdbcType=VARCHAR},
  account_book_id = #{accountBookId,jdbcType=INTEGER},
  financial_account_name = #{financialAccountName,jdbcType=VARCHAR}
where id = #{id,jdbcType=INTEGER}

mapper.select:
SELECT
    id
FROM
    account_financial
WHERE
    user_name = #{userName}
    AND account_book_id = #{accountBookId}
    AND financial_account_name = #{financialAccountName}
