mapper.insert:

insert into account_current (id, user_name, account_book_id,
date, second_category_id, in_ex_status,
amount, account_type_id, remarks
)
values (#{id,jdbcType=INTEGER}, #{userName,jdbcType=VARCHAR}, #{accountBookId,jdbcType=INTEGER},
#{date,jdbcType=TIMESTAMP}, #{secondCategoryId,jdbcType=INTEGER}, #{inExStatus,jdbcType=VARCHAR},
#{amount,jdbcType=DOUBLE}, #{accountTypeId,jdbcType=INTEGER}, #{remarks,jdbcType=VARCHAR}
)

mapper.select:
SELECT
    ac.amount
FROM
    account_current ac
WHERE
    ac.user_name = #{user_name}
    and
    ac.account_book_id = #{accountBookId}
    and
    ac.in_ex_status = #{inExStatus}
    and
    date_format(date, '%Y-%m-%d') = #{dateConverted}

